import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  visibile: boolean = false;  /* *1 */
  eta = 12;
  nome = 'stocazzo';

  constructor(public cognome){
    this.visibile = true;   /* La variabile deve essere init*1 o in linea o nel costruttore */
  }

  ngOnInit(): void {
    
  }

  cambiaVisibilita(){
    this.visibile = !this.visibile;
  }

  utente = {
    nome: 'Robert',
    cognome: 'Neagu'
  }
}


