import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  visibile: boolean = false;  /* La variabile deve essere init o in linea o nel costruttore */ 
  eta = 12;
  nome = 'stocazzo';
  fatture = [];

  constructor(){
    
  }

  ngOnInit(): void {
    this.nome = 'mario'
  }

  cambiaVisibilita(){
    this.visibile = !this.visibile;
  }

  utente = {
    nome: 'Robert',
    cognome: 'Neagu'
  }
}


