import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  visibile: boolean = false;  /* La variabile deve essere init o in linea o nel costruttore */ 
  eta = 12;
  nome = 'sto';
  fatture = [];

  constructor(){
    
  }

  ngOnInit(): void {
    this.nome = 'sto';
    setInterval(() => {
      this.nome = 'cazzo'
    }, 1*1000)
  }

  cambiaVisibilita(){
    this.visibile = !this.visibile;
  }

  utente = {
    nome: 'Robert',
    cognome: 'Neagu'
  }
}


